import { Fragment, useState } from "react";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
export default function Cart({ cartItems, setCartItems }) {
  const [complete, setComplete] = useState("false");

  function increaseQty(item) {
    if (item.product.stock == item.quantity) {
      return;
    }
    const updatedItem = cartItems.map((i) => {
      if (i.product._id == item.product._id) {
        i.quantity++;
      }
      return i;
    });
    setCartItems(updatedItem);
  }
  function decreaseQty(item) {
    if (item.quantity > 1) {
      const updatedItem = cartItems.map((i) => {
        if (i.product._id == item.product._id) {
          i.quantity--;
        }
        return i;
      });
      setCartItems(updatedItem);
    }
  }

  function removeItem(item) {
    const updatedItem = cartItems.filter((i) => {
      //filter the product
      if (i.product._id != item.product._id) {
        return true;
      }
    });
    setCartItems(updatedItem);
  }

  function orderHandler() {
    fetch(process.env.REACT_APP_API_URL + "/order/", {
      method: "POST",
      Headers: { "content-Type": "application/json" },
      body: JSON.stringify(cartItems),
    }).then(() => {
      setCartItems([]);
      setComplete(true);
      toast.success("order success!");
    });
  }

  return cartItems.length > 0 ? (
    <Fragment>
      <div class="container container-fluid">
        <h2 class="mt-5">
          Your Cart: <b>{cartItems.length} items</b>
        </h2>

        <div class="row d-flex justify-content-between">
          <div class="col-12 col-lg-8">
            {cartItems.map((item) => (
              <Fragment>
                <hr />
                <div class="cart-item">
                  <div class="row">
                    <div class="col-4 col-lg-3">
                      <img
                        src={item.product.images[0].image}
                        alt={item.product.name}
                        height="90"
                        width="115"
                      />
                    </div>

                    <div class="col-5 col-lg-3">
                      <Link
                        to={"/product/" + item.product._id}
                        className="btn btn-block"
                      >
                        {item.product.name}
                      </Link>
                    </div>

                    <div class="col-4 col-lg-2 mt-4 mt-lg-0">
                      <p id="card_item_price">${item.product.price}</p>
                    </div>

                    <div class="col-4 col-lg-3 mt-4 mt-lg-0">
                      <div class="stockCounter d-inline">
                        <span
                          class="btn btn-danger minus"
                          onClick={() => decreaseQty(item)}
                        >
                          -
                        </span>
                        <input
                          type="number"
                          class="form-control count d-inline"
                          value={item.quantity}
                          readOnly
                        />

                        <span
                          class="btn btn-primary plus"
                          onClick={() => increaseQty(item)}
                        >
                          +
                        </span>
                      </div>
                    </div>

                    <div class="col-4 col-lg-1 mt-4 mt-lg-0">
                      <i
                        id="delete_cart_item"
                        onClick={() => removeItem(item)}
                        class="fa fa-trash btn btn-danger"
                      ></i>
                    </div>
                  </div>
                </div>
              </Fragment>
            ))}
          </div>

          <div class="col-12 col-lg-3 my-4">
            <div id="order_summary">
              <h4>Order Summary</h4>
              <hr />
              <p>
                Subtotal:{" "}
                <span class="order-summary-values">
                  {cartItems.reduce((acc, item) => acc + item.quantity, 0)}{" "}
                  (Units)
                </span>
              </p>
              <p>
                Est. total:{" "}
                <span class="order-summary-values">
                  ${" "}
                  {cartItems.reduce(
                    (acc, item) => acc + item.product.price * item.quantity,
                    0
                  )}{" "}
                </span>
              </p>

              <hr />
              <button
                id="checkout_btn"
                onClick={orderHandler}
                class="btn btn-primary btn-block"
              >
                Place Order
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  ) : !complete ? (
    <h2 className="mt-5"> Your cart is empty</h2>
  ) : (
    <Fragment>
      <h2 className="mt-5"> order complete </h2>
      <p>your order has been placed successfully</p>
    </Fragment>
  );
}
